import React, { useMemo, useState } from "react";
import { motion } from "framer-motion";
import { Check, Phone, Mail, Shield, Car, HomeIcon, HeartPulse, Landmark, HandCoins, ArrowRight, Star, MessageSquare, HelpCircle, Building2 } from "lucide-react";

// 
// FinanceCo React Website — Single-file starter you can drop into a React app.
// Designed to be exported as a single component for quick preview & iteration.
// TailwindCSS recommended. If Tailwind isn't wired yet, it will still render unstyled HTML.
//
// HOW TO USE IN REAL PROJECT
// - Create a Vite React app (or CRA) and add Tailwind.
// - Put this component into src/App.jsx and export default as shown.
// - Replace placeholder text with your company details.
// - For forms: point ENDPOINTS below to your backend (PHP on cPanel or Node/Serverless).
//
// BACKEND ENDPOINTS (you'll implement later)
// - EMAIL:   /backend/send-email.php (PHPMailer via SMTP)
// - TWILIO:  /backend/send-sms.php    (Twilio SDK)
//

const EMAIL_ENDPOINT = "/backend/send-email.php";
const SMS_ENDPOINT = "/backend/send-sms.php";

const products = [
  {
    key: "life",
    icon: HeartPulse,
    title: "Life Insurance",
    desc: "Secure your family's future with term & whole-life plans tailored to your goals.",
    bullets: ["Term plans", "Tax benefits", "Flexible cover"],
  },
  {
    key: "home-insurance",
    icon: HomeIcon,
    title: "Home Insurance",
    desc: "Protect your home & belongings from fire, theft, floods, and natural calamities.",
    bullets: ["Structure + contents", "Add-on riders", "Cashless claims"],
  },
  {
    key: "vehicle",
    icon: Car,
    title: "Vehicle Insurance",
    desc: "Comprehensive & Third-Party covers for cars and bikes with zero-dep options.",
    bullets: ["Cashless garages", "Roadside assist", "Zero depreciation"],
  },
  {
    key: "home-loan",
    icon: Landmark,
    title: "Home Loans",
    desc: "Competitive interest rates and quick approvals for your dream home.",
    bullets: ["Fast sanction", "Doorstep service", "Flexible tenure"],
  },
  {
    key: "personal-loan",
    icon: HandCoins,
    title: "Personal Loans",
    desc: "Instant personal loans with transparent processing and low documentation.",
    bullets: ["Same-day*", "No collateral", "EMI flexibility"],
  },
];

const faqs = [
  {
    q: "Do I need a backend for email & Twilio?",
    a: "Yes. For security, API keys must be on a server. Use simple PHP on cPanel or a Node/Serverless function. This site posts to /backend endpoints you configure.",
  },
  {
    q: "Can I host this on cPanel (Bluehost)?",
    a: "Yes. Build the React app to static files and upload the dist/build to public_html. Place your PHP endpoints under public_html/backend/.",
  },
  {
    q: "How soon can I go live?",
    a: "Once your content and endpoints are ready, a static brochure site like this can go live as soon as you upload the build to cPanel.",
  },
];

function Stat({ label, value }) {
  return (
    <div className="text-center p-4">
      <div className="text-3xl font-semibold">{value}</div>
      <div className="text-sm text-gray-500">{label}</div>
    </div>
  );
}

function Badge({ children }) {
  return (
    <span className="inline-flex items-center gap-2 rounded-full border px-3 py-1 text-xs">
      <Shield className="w-3.5 h-3.5" /> {children}
    </span>
  );
}

function SectionHeading({ eyebrow, title, subtitle }) {
  return (
    <div className="max-w-2xl mx-auto text-center">
      {eyebrow && (
        <div className="mb-3 inline-block rounded-full bg-gray-100 px-3 py-1 text-xs font-medium uppercase tracking-wider">
          {eyebrow}
        </div>
      )}
      <h2 className="text-3xl md:text-4xl font-semibold leading-tight">{title}</h2>
      {subtitle && <p className="mt-3 text-gray-600">{subtitle}</p>}
    </div>
  );
}

function ProductCard({ icon: Icon, title, desc, bullets }) {
  return (
    <motion.div whileHover={{ y: -4 }} className="group rounded-2xl border p-6 shadow-sm bg-white">
      <div className="flex items-center gap-3">
        <div className="rounded-2xl border p-3">
          <Icon className="w-6 h-6" />
        </div>
        <h3 className="text-lg font-semibold">{title}</h3>
      </div>
      <p className="mt-3 text-sm text-gray-600">{desc}</p>
      <ul className="mt-4 space-y-2 text-sm">
        {bullets.map((b, i) => (
          <li key={i} className="flex items-center gap-2"><Check className="w-4 h-4" />{b}</li>
        ))}
      </ul>
      <button className="mt-5 inline-flex items-center gap-2 rounded-xl border px-4 py-2 text-sm">
        Learn more <ArrowRight className="w-4 h-4" />
      </button>
    </motion.div>
  );
}

function FAQItem({ q, a }) {
  const [open, setOpen] = useState(false);
  return (
    <div className="rounded-2xl border p-4 bg-white">
      <button onClick={() => setOpen(!open)} className="w-full flex items-center justify-between text-left">
        <span className="font-medium">{q}</span>
        <HelpCircle className={`w-5 h-5 transition-transform ${open ? "rotate-180" : ""}`} />
      </button>
      {open && <p className="mt-3 text-gray-600 text-sm">{a}</p>}
    </div>
  );
}

export default function App() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    product: "Life Insurance",
    message: "",
    sendSms: false,
  });
  const [submitting, setSubmitting] = useState(false);
  const [status, setStatus] = useState(null);

  const productOptions = useMemo(() => products.map(p => p.title), []);

  async function handleSubmit(e) {
    e.preventDefault();
    setSubmitting(true);
    setStatus(null);

    try {
      // Build a basic form payload — works with PHP's $_POST via FormData
      const fd = new FormData();
      Object.entries(form).forEach(([k, v]) => fd.append(k, String(v)));

      // 1) Always send an email lead
      const emailRes = await fetch(EMAIL_ENDPOINT, { method: "POST", body: fd });
      const emailOk = emailRes.ok;

      // 2) Optionally trigger SMS via Twilio
      let smsOk = true;
      if (form.sendSms) {
        const smsRes = await fetch(SMS_ENDPOINT, { method: "POST", body: fd });
        smsOk = smsRes.ok;
      }

      if (emailOk && smsOk) {
        setStatus({ type: "success", msg: "Thanks! We\'ll reach out shortly." });
        setForm({ name: "", email: "", phone: "", product: "Life Insurance", message: "", sendSms: false });
      } else {
        setStatus({ type: "error", msg: "Submitted, but there was an issue with one of the services." });
      }
    } catch (err) {
      setStatus({ type: "error", msg: "Something went wrong. Please try again." });
    } finally {
      setSubmitting(false);
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-white to-gray-50 text-gray-900">
      {/* Top bar */}
      <div className="border-b bg-white/70 backdrop-blur supports-[backdrop-filter]:bg-white/60 sticky top-0 z-50">
        <div className="mx-auto max-w-7xl px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="rounded-2xl border p-2">
              <Building2 className="w-5 h-5" />
            </div>
            <span className="font-semibold">Prime Finance & Insurance</span>
          </div>
          <nav className="hidden md:flex items-center gap-6 text-sm">
            <a href="#services" className="hover:opacity-70">Products</a>
            <a href="#why" className="hover:opacity-70">Why Us</a>
            <a href="#process" className="hover:opacity-70">Process</a>
            <a href="#faq" className="hover:opacity-70">FAQ</a>
            <a href="#contact" className="hover:opacity-70">Contact</a>
          </nav>
          <a href="#contact" className="inline-flex items-center gap-2 rounded-xl border px-3 py-2 text-sm">
            <Phone className="w-4 h-4" /> Get Quote
          </a>
        </div>
      </div>

      {/* Hero */}
      <header className="mx-auto max-w-7xl px-4 pt-14 pb-16">
        <div className="grid md:grid-cols-2 gap-8 items-center">
          <div>
            <Badge>IRDAI-compliant partners</Badge>
            <h1 className="mt-4 text-4xl md:text-5xl font-semibold leading-tight">
              Insurance & Loans, <span className="whitespace-nowrap">made simple.</span>
            </h1>
            <p className="mt-4 text-gray-600 max-w-xl">
              From life, home, and vehicle insurance to home and personal loans — compare, choose, and apply with guidance from certified advisors.
            </p>
            <div className="mt-6 flex flex-wrap gap-3">
              <a href="#services" className="rounded-xl border px-4 py-2 inline-flex items-center gap-2">
                Explore Products <ArrowRight className="w-4 h-4" />
              </a>
              <a href="#contact" className="rounded-xl border px-4 py-2 inline-flex items-center gap-2">
                Talk to an Expert <Phone className="w-4 h-4" />
              </a>
            </div>
            <div className="mt-8 grid grid-cols-3 gap-4">
              <Stat label="Happy Clients" value="5,000+" />
              <Stat label="Claim Support" value="24×7" />
              <Stat label="Avg. Rating" value={<span className="inline-flex items-center gap-1"><Star className="w-4 h-4" />4.9</span>} />
            </div>
          </div>
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="relative"
          >
            <div className="rounded-3xl border p-6 shadow-md bg-white">
              <h3 className="text-lg font-semibold flex items-center gap-2"><MessageSquare className="w-5 h-5"/>Quick Quote</h3>
              <p className="text-sm text-gray-600 mt-1">Get a callback for personalised options.</p>
              <form onSubmit={handleSubmit} className="mt-4 grid gap-3">
                <div className="grid grid-cols-2 gap-3">
                  <input required className="rounded-xl border px-3 py-2" placeholder="Full name" value={form.name} onChange={e=>setForm(f=>({...f,name:e.target.value}))}/>
                  <input className="rounded-xl border px-3 py-2" type="email" placeholder="Email (optional)" value={form.email} onChange={e=>setForm(f=>({...f,email:e.target.value}))}/>
                </div>
                <input required className="rounded-xl border px-3 py-2" placeholder="Phone" value={form.phone} onChange={e=>setForm(f=>({...f,phone:e.target.value}))}/>
                <select className="rounded-xl border px-3 py-2" value={form.product} onChange={e=>setForm(f=>({...f,product:e.target.value}))}>
                  {productOptions.map(o => <option key={o} value={o}>{o}</option>)}
                </select>
                <textarea className="rounded-xl border px-3 py-2" rows={3} placeholder="Tell us a bit about your need" value={form.message} onChange={e=>setForm(f=>({...f,message:e.target.value}))}/>
                <label className="flex items-center gap-2 text-sm">
                  <input type="checkbox" checked={form.sendSms} onChange={e=>setForm(f=>({...f,sendSms:e.target.checked}))} />
                  Send me status updates via SMS (Twilio)
                </label>
                <button disabled={submitting} className="rounded-xl border px-4 py-2 text-sm inline-flex items-center gap-2 justify-center">
                  {submitting ? "Submitting..." : "Request Callback"}
                </button>
                {status && (
                  <div className={`text-sm ${status.type === "success" ? "text-green-600" : "text-red-600"}`}>{status.msg}</div>
                )}
              </form>
            </div>
          </motion.div>
        </div>
      </header>

      {/* Services */}
      <section id="services" className="mx-auto max-w-7xl px-4 py-12">
        <SectionHeading
          eyebrow="Products"
          title="Insurance & Loan Solutions"
          subtitle="Transparent terms, expert guidance, and claim support when it matters."
        />
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 mt-8">
          {products.map((p) => (
            <ProductCard key={p.key} icon={p.icon} title={p.title} desc={p.desc} bullets={p.bullets} />
          ))}
        </div>
      </section>

      {/* Why Us */}
      <section id="why" className="mx-auto max-w-7xl px-4 py-12">
        <SectionHeading
          eyebrow="Why Choose Us"
          title="We simplify complex financial decisions"
          subtitle="Licensed partners, unbiased comparisons, and support beyond purchase."
        />
        <div className="mt-8 grid md:grid-cols-3 gap-6">
          {["IRDAI-compliant partners", "Paperwork & claims assistance", "Dedicated relationship manager"].map((point, i) => (
            <div key={i} className="rounded-2xl border p-6 bg-white">
              <div className="rounded-2xl border p-3 w-fit mb-3"><Shield className="w-5 h-5"/></div>
              <div className="font-medium">{point}</div>
              <p className="text-sm text-gray-600 mt-1">We ensure compliance and provide hand-holding at every step so you never feel lost.</p>
            </div>
          ))}
        </div>
      </section>

      {/* Process */}
      <section id="process" className="mx-auto max-w-7xl px-4 py-12">
        <SectionHeading eyebrow="How it works" title="From quote to cover in 3 easy steps" />
        <div className="grid md:grid-cols-3 gap-6 mt-8">
          {["Share your need", "Compare best options", "Apply & get support"].map((step, idx) => (
            <div key={idx} className="rounded-2xl border p-6 bg-white">
              <div className="text-3xl font-semibold">{idx + 1}</div>
              <div className="mt-2 font-medium">{step}</div>
              <p className="text-sm text-gray-600 mt-1">We do the heavy lifting so you get the right cover and the right loan, fast.</p>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonials */}
      <section className="mx-auto max-w-7xl px-4 py-12">
        <SectionHeading eyebrow="Testimonials" title="What our clients say" />
        <div className="mt-8 grid md:grid-cols-3 gap-6">
          {["Claim was super smooth.", "Got the best rate for my home loan!", "Clear guidance, zero pushiness."].map((t, i) => (
            <div key={i} className="rounded-2xl border p-6 bg-white">
              <div className="flex items-center gap-2 text-yellow-600"><Star className="w-4 h-4"/><Star className="w-4 h-4"/><Star className="w-4 h-4"/><Star className="w-4 h-4"/><Star className="w-4 h-4"/></div>
              <p className="mt-3 text-sm text-gray-700">“{t}”</p>
              <div className="mt-4 text-sm text-gray-500">— Happy Customer</div>
            </div>
          ))}
        </div>
      </section>

      {/* FAQ */}
      <section id="faq" className="mx-auto max-w-7xl px-4 py-12">
        <SectionHeading eyebrow="FAQ" title="You might be wondering…" />
        <div className="mt-8 grid md:grid-cols-2 gap-4">
          {faqs.map((f, i) => (
            <FAQItem key={i} q={f.q} a={f.a} />
          ))}
        </div>
      </section>

      {/* Contact */}
      <section id="contact" className="mx-auto max-w-7xl px-4 py-12">
        <SectionHeading eyebrow="Contact" title="Let’s talk" subtitle="Prefer WhatsApp or a phone call? Mention it in your message." />
        <div className="mt-8 grid md:grid-cols-2 gap-6">
          <div className="rounded-2xl border p-6 bg-white">
            <h3 className="font-semibold">Office</h3>
            <p className="text-sm text-gray-600 mt-2">Hyderabad, Telangana (Update with your address)</p>
            <div className="mt-4 space-y-2 text-sm">
              <div className="flex items-center gap-2"><Phone className="w-4 h-4"/> +91-00000 00000</div>
              <div className="flex items-center gap-2"><Mail className="w-4 h-4"/> info@yourcompany.com</div>
            </div>
            <div className="mt-6 grid grid-cols-2 gap-3">
              <div className="rounded-2xl border p-4">
                <div className="text-2xl font-semibold">10+</div>
                <div className="text-sm text-gray-500">Insurer & lender partners</div>
              </div>
              <div className="rounded-2xl border p-4">
                <div className="text-2xl font-semibold">48h</div>
                <div className="text-sm text-gray-500">Typical loan sanction*</div>
              </div>
            </div>
          </div>

          <div className="rounded-2xl border p-6 bg-white">
            <h3 className="font-semibold">Send a message</h3>
            <form onSubmit={handleSubmit} className="mt-4 grid gap-3">
              <input required className="rounded-xl border px-3 py-2" placeholder="Full name" value={form.name} onChange={e=>setForm(f=>({...f,name:e.target.value}))}/>
              <div className="grid grid-cols-2 gap-3">
                <input required className="rounded-xl border px-3 py-2" placeholder="Phone" value={form.phone} onChange={e=>setForm(f=>({...f,phone:e.target.value}))}/>
                <input className="rounded-xl border px-3 py-2" type="email" placeholder="Email (optional)" value={form.email} onChange={e=>setForm(f=>({...f,email:e.target.value}))}/>
              </div>
              <select className="rounded-xl border px-3 py-2" value={form.product} onChange={e=>setForm(f=>({...f,product:e.target.value}))}>
                {productOptions.map(o => <option key={o} value={o}>{o}</option>)}
              </select>
              <textarea className="rounded-xl border px-3 py-2" rows={4} placeholder="What can we help you with?" value={form.message} onChange={e=>setForm(f=>({...f,message:e.target.value}))}/>
              <label className="flex items-center gap-2 text-sm">
                <input type="checkbox" checked={form.sendSms} onChange={e=>setForm(f=>({...f,sendSms:e.target.checked}))} />
                Send me status updates via SMS (Twilio)
              </label>
              <button disabled={submitting} className="rounded-xl border px-4 py-2 text-sm inline-flex items-center gap-2 justify-center">
                {submitting ? "Submitting..." : "Submit"}
              </button>
              {status && (
                <div className={`text-sm ${status.type === "success" ? "text-green-600" : "text-red-600"}`}>{status.msg}</div>
              )}
            </form>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="border-t bg-white">
        <div className="mx-auto max-w-7xl px-4 py-10 grid md:grid-cols-4 gap-6">
          <div>
            <div className="flex items-center gap-2"><Building2 className="w-5 h-5"/><span className="font-semibold">Prime Finance & Insurance</span></div>
            <p className="text-sm text-gray-600 mt-2">Building wealth. Ensuring security.</p>
          </div>
          <div>
            <div className="font-semibold">Products</div>
            <ul className="mt-3 space-y-2 text-sm text-gray-600">
              {products.map(p => <li key={p.key}><a href="#services" className="hover:underline">{p.title}</a></li>)}
            </ul>
          </div>
          <div>
            <div className="font-semibold">Company</div>
            <ul className="mt-3 space-y-2 text-sm text-gray-600">
              <li><a href="#why" className="hover:underline">Why Us</a></li>
              <li><a href="#process" className="hover:underline">How it works</a></li>
              <li><a href="#faq" className="hover:underline">FAQ</a></li>
            </ul>
          </div>
          <div>
            <div className="font-semibold">Contact</div>
            <ul className="mt-3 space-y-2 text-sm text-gray-600">
              <li className="flex items-center gap-2"><Phone className="w-4 h-4"/> +91-00000 00000</li>
              <li className="flex items-center gap-2"><Mail className="w-4 h-4"/> info@yourcompany.com</li>
            </ul>
          </div>
        </div>
        <div className="border-t">
          <div className="mx-auto max-w-7xl px-4 py-6 text-xs text-gray-500 flex flex-wrap items-center justify-between gap-2">
            <div>© {new Date().getFullYear()} Prime Finance & Insurance. All rights reserved.</div>
            <div className="flex items-center gap-4">
              <a href="#" className="hover:underline">Privacy Policy</a>
              <a href="#" className="hover:underline">Terms</a>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}
