import "./Css/Service"

const services = [
  { title: "Life Insurance", desc: "Protect your family’s future with our tailored plans." },
  { title: "Home Insurance", desc: "Secure your home and valuables." },
  { title: "Vehicle Insurance", desc: "Comprehensive coverage for your car or bike." },
  { title: "Home Loans", desc: "Affordable EMIs to make your dream home a reality." },
  { title: "Personal Loans", desc: "Quick approvals for your financial needs." }
];

export default function Services() {
  return (
    <section className="py-16 text-center bg-gray-100">
      <h2 className="text-3xl font-bold mb-10">Our Services</h2>
      <div className="grid md:grid-cols-3 gap-8 max-w-5xl mx-auto">
        {services.map((s, i) => (
          <div key={i} className="p-6 bg-white shadow-lg rounded-xl">
            <h3 className="text-xl font-semibold">{s.title}</h3>
            <p className="mt-2 text-gray-600">{s.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
