import React from "react";
import './'

const Testimonials = () => {
  const testimonials = [
    { name: "Rahul Sharma", feedback: "FinanceCo made my home loan process simple and stress-free. Highly recommended!" },
    { name: "Priya Mehta", feedback: "Great service! They explained all insurance options clearly and helped me choose the best." },
    { name: "Arjun Reddy", feedback: "I got my vehicle insurance instantly without any hassle. Superb support!" },
  ];

  return (
    <section id="testimonials" className="py-16">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold mb-12">What Our Clients Say</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {testimonials.map((t, index) => (
            <div key={index} className="bg-gray-100 rounded-lg shadow-md p-6">
              <p className="italic text-gray-700 mb-4">"{t.feedback}"</p>
              <h3 className="font-semibold text-blue-600">- {t.name}</h3>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
